INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16001, 'Assertivit√†', 'La capacit√† di esprimere in modo chiaro ed efficace le proprie emozioni, opinioni,bisogni e punti di vista, senza ledere quelle altrui', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16002, 'Autostima', 'La valutazione e apprezzamento di se stessi, tramite l''autoapprovazione del proprio valore personale', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16003, 'Autoefficacia', 'La consapevolezza di esser capaci di fare, sentire, esprimere, essere o divenire qualcosa', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16004, 'Consapevolezza', 'La capacit√† di essere a conoscenza di ci√≤ che viene percepito e delle proprie risposte comportamentali', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16005, 'Ascolto attivo', 'Saper ascoltare con un elevato grado di attenzione e partecipazione comunicativa. Saper osservare bene l''interlocutore, percependo anche il suo linguaggio non verbale e paraverbale', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16006, 'Intelligenza emotiva', 'Saper riconoscere, identificare ed etichettare nel modo appropriato e gestire le proprie emozioni e quelle degli altri, allo scopo di raggiungere determinati obiettivi', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16007, 'Empatia', 'La capacit√† di riconoscere le emozioni degli altri come se fossero proprie, calandosi nella realt√† altrui per comprenderne punti di vista, pensieri, sentimenti, emozioni', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16008, 'Flessibilit√†', 'La capacit√† di adattamento a situazioni impreviste, al cambiamento', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16009, 'Negoziazione', 'Saper risolvere le divergenze, raggiungendo un compromesso o un accordo evitando discussioni e controversie', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16010, 'Gestione dei conflitti', 'Saper limitare gli aspetti negativi di un conflitto, aumentandone al contempo gli aspetti positivi e costruttivi', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16011, 'Resilienza', 'La capacit√† di affrontare e superare un evento traumatico o un periodo di difficolt√†', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16012, 'Resistenza allo stress', 'L''essere in grado di reagire positivamente alla pressione lavorativa mantenendo il controllo, rimanendo focalizzati sulle priorit√†, senza trasferire sui colleghi le proprie eventuali tensioni', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16013, 'Problem solving', 'La serie di attivit√† di risoluzione di un problema che sono dinamiche (richiedono delle prime azioni da svolgere nell''ambiente, a partire dalle quali sono poi prese successive decisioni), dipendenti dal tempo (le decisioni devono essere prese al momento giusto in relazione all''ambiente) e complesse (la maggior parte delle variabili non sono correlate l''una all''altra)', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16014, 'Pensiero laterale', 'La modalit√† di risoluzione di problemi logici che prevede un approccio indiretto, l''osservazione del problema da diverse angolazioni, contrapposta alla tradizionale modalit√† che prevede concentrazione su una soluzione diretta del problema', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16015, 'Pensiero analitico', 'L''abilit√† di pensare in maniera dettagliata, approfondita e sistematica per risolvere dei problemi', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16016, 'Pensiero critico', 'La capacit√† di pensare in modo chiaro e razionale, comprendendo la connessione logica tra le idee', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16017, 'Concentrazione', 'La capacit√† volontaria di fissare il pensiero su un oggetto, sul compimento di un gesto, un''azione predeterminata ed elaborata dalla propria mente, o semplicemente su un argomento specifico', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16018, 'Brainstorming', 'La capacit√† di  far emergere idee volte alla risoluzione di un problema', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16019, 'Attitudine di vendita', 'La capacit√† di sviluppare strategie funzionali all''individuazione della giusta soluzione per problemi complessi dei clienti, generando al contempo entusiasmo per la soluzione che viene proposta, favorendo l''impegno delle parti coinvolte', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16020, 'Pianificazione', 'Il riuscire a pensare al futuro, per anticipare mentalmente il modo corretto di eseguire un compito. Il saper selezionare delle attivit√† necessarie a raggiungere un obiettivo, il saperne decidere l''ordine e il saper creare un piano', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16021, 'Gestione del tempo', 'Il saper strutturare in maniera sistematica l''allocazione temporale e la distribuzione di richieste concorrenti, basandosi su priorit√†', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16022, 'Creativit√†', 'La capacit√† di rprodurre idee nuove, insolite, in maniera abbondante, percorrendo fino alla fine una strada ideativa con ricchezza di particolari, selezionando le idee e organizzandole in forme nuove', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16023, 'Leadership', 'La capacit√† di far leva sulla consapevolezza di se, sulla gestione delle proprie emozioni, sulla comprensione dei sentimenti degli altri, sulla possibilit√† di creare risonanza, al fine di motivare un gruppo di persone ad agire alla ricerca di un obiettivo comune', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16024, 'Gestione del team', 'Il saper coordinare un gruppo di persone al fine dello svolgimento di un compito con spirito positivo, sviluppando al tempo stesso il senso di responsabilit√† e potenziando le capacit√† professionali delle persone stesse', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16025, 'Collaborazione', 'L''essere in grado di partecipare attivamente al compimento di un lavoro o allo svolgimento di una attivit√†', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16026, 'Delega', 'La capacit√† di individuare una persona compatibile, per competenza e profilo, verso una responsabilit√† lavorativa e, successivamente, di attuare, in maniera partecipata, un passaggio di responsabilit√† verso quella persona', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16027, 'Coaching', 'La capacit√† di accompagnare una persona verso una definizione dei risultati che desidera raggiungere, una comprensione delle sue motivazioni, verso il riconoscimento di se stesso, l''individuazione delle proprie competenze, verso il compimento di azioni necessarie a conseguire il raggiungimento dei risultati individuati e di stimolare l''apprendimento continuo tramite l''esperienza', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16028, 'Tutoring', 'La capacit√† di aiutare e sostenere delle persone durante il processo di apprendimento, in modo interattivo, intenzionale e sistematico', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16029, 'Team building', 'L''essere in grado di individuare, progettare e realizzare attivit√† e procedimenti, formativi o educativi, per far interagire un gruppo di persone allo scopo di migliorarne la capacit√† di lavorare in squadra', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16030, 'Responsabilit√†', 'L''essere affidabile. Il riuscire a portare a termine dei compiti ricevuti, il rispettare tempistiche e priorit√† con disciplina, dimostrando attenzione alle esigenze organizzative', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16031, 'Fornire feedback', 'La capacit√† di formulare un feedback in maniera corretta e specifica, in riferimento a comportamenti e non a persone, evidenziando le conseguenze del comportamento e cercando delle azioni di miglioramento', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16032, 'Accettazione dei feedback', 'La capacit√† di cogliere in maniera positiva i significati di un feedback, sia positivo che negativo. L''essere in grado di intraprendere azioni migliorative in seguito ad un feedback e la propensione alla ricerca di feedback', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16033, 'Public speaking', 'L''essere in grado di eseguire un discorso dal vivo, faccia a faccia, davanti ad un pubblico di ascoltatori, in maniera chiara, positiva, coinvolgente, controllando le proprie ansie e le proprie preoccupazioni, gestendo l''eventuale noia del pubblico, trasferendo in maniera efficace il contenuto del discorso', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16034, 'Storytelling', 'L''essere in grado di utilizzare l''arte della narrazione, del racconto, sia in forma orale che scritta, per comunicare in maniera efficace e coinvolgente i propri prodotti o i propri servizi', 'SOFT', 16, 8);
INSERT INTO skill (ID, NAME, DESCRIPTION, TYPE, AreaId, ScaleId) VALUES (16035, 'Business writing', 'Il saper organizzare e decidere il contenuto, il saper scegliere le parole da usare, il saper modulare il linguaggio all''interno di un testo, allo scopo di raggiungere degli obiettivi di business', 'SOFT', 16, 8);
